import React, { useState, useEffect } from 'react';
import { 
  TrendingUp, 
  BarChart3, 
  RefreshCw,
  Calendar,
  DollarSign
} from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

function PriceChart({ onNotification }) {
  const [selectedCrypto, setSelectedCrypto] = useState('BTC');
  const [timeRange, setTimeRange] = useState('24h');
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPrice, setCurrentPrice] = useState(null);
  const [dataSource, setDataSource] = useState('real'); // 'real' or 'demo'

  const cryptos = [
    { symbol: 'BTC', name: 'Bitcoin' },
    { symbol: 'ETH', name: 'Ethereum' },
    { symbol: 'ADA', name: 'Cardano' },
    { symbol: 'DOT', name: 'Polkadot' },
    { symbol: 'LINK', name: 'Chainlink' },
    { symbol: 'UNI', name: 'Uniswap' },
    { symbol: 'AAVE', name: 'Aave' },
    { symbol: 'SOL', name: 'Solana' },
    { symbol: 'MATIC', name: 'Polygon' },
    { symbol: 'AVAX', name: 'Avalanche' }
  ];

  const timeRanges = [
    { value: '1h', label: '1 Hour' },
    { value: '24h', label: '24 Hours' },
    { value: '7d', label: '7 Days' },
    { value: '30d', label: '30 Days' }
  ];

  // CoinGecko API mapping for cryptocurrency IDs
  const coinGeckoMapping = {
    'BTC': 'bitcoin',
    'ETH': 'ethereum',
    'ADA': 'cardano',
    'DOT': 'polkadot',
    'LINK': 'chainlink',
    'UNI': 'uniswap',
    'AAVE': 'aave',
    'SOL': 'solana',
    'MATIC': 'matic-network',
    'AVAX': 'avalanche-2'
  };

  // Fetch real historical data from CoinGecko API
  const fetchRealChartData = async (symbol, range) => {
    const coinId = coinGeckoMapping[symbol];
    if (!coinId) {
      throw new Error(`Unsupported cryptocurrency: ${symbol}`);
    }

    // Map time ranges to CoinGecko API parameters
    const rangeMapping = {
      '1h': { days: 1 },
      '24h': { days: 1 },
      '7d': { days: 7 },
      '30d': { days: 30 }
    };

    const rangeConfig = rangeMapping[range];
    if (!rangeConfig) {
      throw new Error(`Unsupported time range: ${range}`);
    }

    try {
      // Use a more reliable endpoint with retry mechanism
      const url = `https://api.coingecko.com/api/v3/coins/${coinId}/market_chart`;
      const params = new URLSearchParams({
        vs_currency: 'usd',
        days: rangeConfig.days.toString()
      });

      // Add retry mechanism
      let lastError;
      for (let attempt = 1; attempt <= 3; attempt++) {
        try {
          const response = await fetch(`${url}?${params}`, {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
              'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
              'Cache-Control': 'no-cache'
            }
          });

          if (!response.ok) {
            if (response.status === 429) {
              // Rate limited, wait and retry
              await new Promise(resolve => setTimeout(resolve, 1000 * attempt));
              continue;
            }
            throw new Error(`CoinGecko API error: ${response.status} ${response.statusText}`);
          }

          const data = await response.json();
          
          if (!data.prices || !Array.isArray(data.prices)) {
            throw new Error('Invalid data format from CoinGecko API');
          }
          
          // Transform CoinGecko data to our chart format
          const chartData = data.prices.map(([timestamp, price]) => ({
            time: new Date(timestamp).toISOString(),
            price: price,
            volume: data.total_volumes ? data.total_volumes.find(v => v[0] === timestamp)?.[1] || 0 : 0
          }));

          return chartData;
        } catch (error) {
          lastError = error;
          console.error(`Attempt ${attempt} failed:`, error);
          if (attempt < 3) {
            await new Promise(resolve => setTimeout(resolve, 1000 * attempt));
          }
        }
      }
      
      throw lastError;
    } catch (error) {
      console.error('Error fetching real chart data:', error);
      throw error;
    }
  };

  // Fallback mock data generation (used when API fails)
  const generateMockData = (symbol, range) => {
    const data = [];
    const now = new Date();
    const intervals = {
      '1h': { count: 60, minutes: 1 },
      '24h': { count: 24, hours: 1 },
      '7d': { count: 168, hours: 1 },
      '30d': { count: 30, days: 1 }
    };

    const interval = intervals[range];
    if (!interval) return [];

    let basePrice = 50000; // Default base price
    if (symbol === 'ETH') basePrice = 3000;
    if (symbol === 'ADA') basePrice = 0.5;
    if (symbol === 'DOT') basePrice = 20;
    if (symbol === 'LINK') basePrice = 15;
    if (symbol === 'UNI') basePrice = 8;
    if (symbol === 'AAVE') basePrice = 100;
    if (symbol === 'SOL') basePrice = 25;
    if (symbol === 'MATIC') basePrice = 0.8;
    if (symbol === 'AVAX') basePrice = 35;

    for (let i = interval.count - 1; i >= 0; i--) {
      const timestamp = new Date(now);
      
      if (interval.minutes) {
        timestamp.setMinutes(timestamp.getMinutes() - (i * interval.minutes));
      } else if (interval.hours) {
        timestamp.setHours(timestamp.getHours() - (i * interval.hours));
      } else if (interval.days) {
        timestamp.setDate(timestamp.getDate() - (i * interval.days));
      }

      // Generate realistic price movement
      const volatility = 0.02; // 2% volatility
      const change = (Math.random() - 0.5) * volatility;
      basePrice = basePrice * (1 + change);

      data.push({
        time: timestamp.toISOString(),
        price: Math.max(basePrice, 0.01), // Ensure positive price
        volume: Math.random() * 1000000
      });
    }

    return data;
  };

  const fetchChartData = async () => {
    setLoading(true);
    
    try {
      // Fetch real historical data from CoinGecko API
      const realData = await fetchRealChartData(selectedCrypto, timeRange);
      setChartData(realData);
      setDataSource('real');
      
      // Set current price (last data point)
      if (realData.length > 0) {
        setCurrentPrice(realData[realData.length - 1].price);
      }
      
    } catch (error) {
      console.error('Error fetching chart data:', error);
      onNotification('Failed to fetch real-time chart data from CoinGecko API', 'error');
      
      // Fallback to mock data if API fails
      try {
        const mockData = generateMockData(selectedCrypto, timeRange);
        setChartData(mockData);
        setDataSource('demo');
        if (mockData.length > 0) {
          setCurrentPrice(mockData[mockData.length - 1].price);
        }
        onNotification('Using demo data due to API error', 'warning');
      } catch (mockError) {
        console.error('Mock data generation failed:', mockError);
        setChartData([]);
        setDataSource('error');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchChartData();
  }, [selectedCrypto, timeRange]);

  const formatPrice = (price) => {
    if (!price) return '$0.00';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 6
    }).format(price);
  };

  const formatTime = (timeStr) => {
    const date = new Date(timeStr);
    if (timeRange === '1h') {
      return date.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });
    } else if (timeRange === '24h') {
      return date.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });
    } else if (timeRange === '7d') {
      return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    } else {
      return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    }
  };

  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-white p-3 rounded-lg shadow-lg border border-gray-200">
          <p className="text-sm text-gray-600">{formatTime(label)}</p>
          <p className="text-lg font-semibold text-gray-900">
            {formatPrice(payload[0].value)}
          </p>
        </div>
      );
    }
    return null;
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Price Charts</h1>
          <p className="text-gray-600 mt-1">
            Analyze cryptocurrency price movements over time
          </p>
        </div>
        <button
          onClick={fetchChartData}
          disabled={loading}
          className="btn-secondary flex items-center space-x-2"
        >
          <RefreshCw className={`w-4 h-4 ${loading ? 'animate-spin' : ''}`} />
          <span>Refresh</span>
        </button>
      </div>

      {/* Controls */}
      <div className="card">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between space-y-4 md:space-y-0">
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <DollarSign className="w-5 h-5 text-gray-600" />
              <label className="text-sm font-medium text-gray-700">Cryptocurrency:</label>
            </div>
            <select
              value={selectedCrypto}
              onChange={(e) => setSelectedCrypto(e.target.value)}
              className="select-field w-auto"
            >
              {cryptos.map(crypto => (
                <option key={crypto.symbol} value={crypto.symbol}>
                  {crypto.symbol} - {crypto.name}
                </option>
              ))}
            </select>
          </div>

          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <Calendar className="w-5 h-5 text-gray-600" />
              <label className="text-sm font-medium text-gray-700">Time Range:</label>
            </div>
            <select
              value={timeRange}
              onChange={(e) => setTimeRange(e.target.value)}
              className="select-field w-auto"
            >
              {timeRanges.map(range => (
                <option key={range.value} value={range.value}>
                  {range.label}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Current Price */}
      {currentPrice && (
        <div className="card bg-gradient-to-r from-primary-50 to-blue-50">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-semibold text-gray-900">
                {cryptos.find(c => c.symbol === selectedCrypto)?.name} ({selectedCrypto})
              </h3>
              <p className="text-3xl font-bold text-primary-600 mt-1">
                {formatPrice(currentPrice)}
              </p>
            </div>
            <div className="flex items-center space-x-2">
              <TrendingUp className="w-8 h-8 text-crypto-green" />
              <div className="text-right">
                <p className="text-sm text-gray-600">24h Change</p>
                <p className="text-lg font-semibold text-crypto-green">
                  +2.34%
                </p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Chart */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-lg font-semibold text-gray-900 flex items-center space-x-2">
            <BarChart3 className="w-5 h-5" />
            <span>Price Chart</span>
          </h2>
          <div className="text-sm text-gray-600">
            {timeRanges.find(r => r.value === timeRange)?.label}
          </div>
        </div>

        {loading ? (
          <div className="flex items-center justify-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
          </div>
        ) : chartData.length > 0 ? (
          <div className="h-64">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={chartData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                <XAxis 
                  dataKey="time" 
                  tickFormatter={formatTime}
                  stroke="#666"
                  fontSize={12}
                />
                <YAxis 
                  tickFormatter={(value) => `$${value.toLocaleString()}`}
                  stroke="#666"
                  fontSize={12}
                />
                <Tooltip content={<CustomTooltip />} />
                <Line 
                  type="monotone" 
                  dataKey="price" 
                  stroke="#3b82f6" 
                  strokeWidth={2}
                  dot={false}
                  activeDot={{ r: 4, stroke: '#3b82f6', strokeWidth: 2 }}
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        ) : (
          <div className="flex items-center justify-center h-64 text-gray-500">
            <div className="text-center">
              <BarChart3 className="w-12 h-12 mx-auto mb-4 text-gray-400" />
              <p>No chart data available</p>
            </div>
          </div>
        )}
      </div>

      {/* Chart Info */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="card">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-green-100 rounded-lg">
              <TrendingUp className="w-5 h-5 text-green-600" />
            </div>
            <div>
              <p className="text-sm font-medium text-gray-600">Highest Price</p>
              <p className="text-lg font-semibold text-gray-900">
                {chartData.length > 0 ? formatPrice(Math.max(...chartData.map(d => d.price))) : 'N/A'}
              </p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-red-100 rounded-lg">
              <TrendingUp className="w-5 h-5 text-red-600 rotate-180" />
            </div>
            <div>
              <p className="text-sm font-medium text-gray-600">Lowest Price</p>
              <p className="text-lg font-semibold text-gray-900">
                {chartData.length > 0 ? formatPrice(Math.min(...chartData.map(d => d.price))) : 'N/A'}
              </p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-blue-100 rounded-lg">
              <BarChart3 className="w-5 h-5 text-blue-600" />
            </div>
            <div>
              <p className="text-sm font-medium text-gray-600">Data Points</p>
              <p className="text-lg font-semibold text-gray-900">
                {chartData.length}
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Data Source Info */}
      <div className={`card ${
        dataSource === 'real' ? 'bg-green-50 border-green-200' :
        dataSource === 'demo' ? 'bg-yellow-50 border-yellow-200' :
        'bg-red-50 border-red-200'
      }`}>
        <div className="flex items-start space-x-3">
          <div className="flex-shrink-0">
            <div className={`w-6 h-6 rounded-full flex items-center justify-center ${
              dataSource === 'real' ? 'bg-green-100' :
              dataSource === 'demo' ? 'bg-yellow-100' :
              'bg-red-100'
            }`}>
              <span className={`text-sm ${
                dataSource === 'real' ? 'text-green-600' :
                dataSource === 'demo' ? 'text-yellow-600' :
                'text-red-600'
              }`}>
                {dataSource === 'real' ? '✓' : dataSource === 'demo' ? '⚠' : '✗'}
              </span>
            </div>
          </div>
          <div>
            <h3 className={`text-sm font-semibold ${
              dataSource === 'real' ? 'text-green-900' :
              dataSource === 'demo' ? 'text-yellow-900' :
              'text-red-900'
            }`}>
              {dataSource === 'real' ? 'Real-Time Data' : 
               dataSource === 'demo' ? 'Demo Data' : 
               'Data Error'}
            </h3>
            <p className={`text-sm mt-1 ${
              dataSource === 'real' ? 'text-green-700' :
              dataSource === 'demo' ? 'text-yellow-700' :
              'text-red-700'
            }`}>
              {dataSource === 'real' ? 
                'This chart displays real historical price data fetched from CoinGecko API. Data includes actual price movements, trading volumes, and market trends for accurate cryptocurrency analysis.' :
               dataSource === 'demo' ?
                'Using simulated data due to API limitations. Real data would be fetched from CoinGecko API in a production environment.' :
                'Unable to load chart data. Please try refreshing or check your internet connection.'
              }
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PriceChart;
